function ready(e){"loading"!=document.readyState?e():document.addEventListener("DOMContentLoaded",e)}function add_event_listeners_to_form(){const e=document.getElementById("digrest-form"),t=document.getElementById("textbox-url"),n=document.getElementById("select-request");t.addEventListener("change",(()=>{e.action=t.value}));document.getElementById("btn-submit").addEventListener("click",(e=>{e.preventDefault(),http_request(t.value,n.value)}));const d=document.getElementById("protocol-switch");d.addEventListener("click",(()=>{d.innerText="http://"==d.innerText?"https://":"http://"}))}async function http_request(e,t){const n=document.getElementById("textarea-response");try{const d=await fetch(document.getElementById("protocol-switch").innerText+e,{method:t}),o=await d.text();n.value=o}catch(e){n.value=e}}ready(add_event_listeners_to_form);