function ready(e){"loading"!=document.readyState?e():document.addEventListener("DOMContentLoaded",e)}function add_event_listeners_to_form(){document.getElementById("btn-submit").addEventListener("click",submitForm);const e=document.getElementById("protocol-switch");e.addEventListener("click",toggleProtocol),e.addEventListener("click",updateFullUrl);document.getElementById("btn-add-row").addEventListener("click",addRowToTable);document.getElementById("btn-clear-table").addEventListener("click",clearTable);document.getElementById("textbox-url").addEventListener("keyup",updateFullUrl),addRowToTable()}function submitForm(e){const t=document.getElementById("select-request"),n=document.getElementById("textbox-full-url");e.preventDefault(),http_request(n.value,t.value)}function toggleProtocol(){const e=document.getElementById("protocol-switch");e.innerText="http://"==e.innerText?"https://":"http://",changeFormAction()}function clearTable(){const e=document.getElementById("table-parameters").getElementsByTagName("tbody")[0];for(;e.childElementCount>0;)e.removeChild(e.lastChild);updateFullUrl(),addRowToTable()}function addRowToTable(){const e=document.getElementById("table-parameters").getElementsByTagName("tbody")[0],t=e.childElementCount;let n=document.createElement("tr"),d=document.createElement("td"),l=document.createElement("input");l.type="checkbox",l.checked=!0,l.classList.add("form-control-sm"),l.id="checkbox-active-parameter-"+t,d.appendChild(l),n.appendChild(d);let a=document.createElement("td");a.id="parameter-key-"+t,a.contentEditable=!0,a.addEventListener("keyup",updateFullUrl),n.appendChild(a);let o=document.createElement("td");o.id="parameter-value-"+t,o.contentEditable=!0,o.addEventListener("keyup",updateFullUrl),n.appendChild(o);let c=document.createElement("td");c.id="parameter-description-"+t,c.contentEditable=!0,n.appendChild(c),e.appendChild(n)}function updateFullUrl(){const e=document.getElementById("textbox-url"),t=document.getElementById("textbox-full-url"),n=document.getElementById("table-parameters").getElementsByTagName("tbody")[0],d=document.getElementById("protocol-switch");if(t.value=d.innerText+e.value,n.firstChild&&""!=document.getElementById("parameter-key-0").innerText){t.value+="?";for(let[e,d]of n.childNodes.entries())document.getElementById("checkbox-active-parameter-"+e).checked&&(t.value+=document.getElementById("parameter-key-"+e).innerText,""!=document.getElementById("parameter-value-"+e).innerText&&(t.value+="="+document.getElementById("parameter-value-"+e).innerText),d!=n.lastChild&&(t.value+="&"))}}async function http_request(e,t){const n=document.getElementById("textarea-response");try{const d=await fetch(e,{method:t}),l=await d.json();n.value=JSON.stringify(l,null,4)}catch(e){n.value=e}}ready(add_event_listeners_to_form);