function ready(e){"loading"!=document.readyState?e():document.addEventListener("DOMContentLoaded",e)}function add_event_listeners_to_form(){document.getElementById("btn-submit").addEventListener("click",submitForm);const e=document.getElementById("protocol-switch");e.addEventListener("click",toggleProtocol),e.addEventListener("click",updateFullUrl);const t=document.getElementById("btn-parameter-add-row");t.addEventListener("click",addRowToTable),t.tableId="table-parameters";const n=document.getElementById("btn-header-add-row");n.addEventListener("click",addRowToTable),n.tableId="table-header";const d=document.getElementById("btn-parameter-clear-table");d.addEventListener("click",clearTable),d.tableId="table-parameters";const l=document.getElementById("btn-header-clear-table");l.addEventListener("click",clearTable),l.tableId="table-header";document.getElementById("textbox-url").addEventListener("keyup",updateFullUrl);[{currentTarget:{tableId:"table-parameters"}},{currentTarget:{tableId:"table-header"}}].forEach((e=>{addRowToTable(e)})),updateFullUrl()}function submitForm(e){const t=document.getElementById("select-request"),n=document.getElementById("textbox-full-url");e.preventDefault(),http_request(n.value,t.value)}function toggleProtocol(){const e=document.getElementById("protocol-switch");e.innerText="http://"==e.innerText?"https://":"http://"}function clearTable(e){if(null!=e){const t=document.getElementById(e.currentTarget.tableId).getElementsByTagName("tbody")[0];for(;t.childElementCount>0;)t.removeChild(t.lastChild);addRowToTable(e)}}function addRowToTable(e){if(null!=e){const t=document.getElementById(e.currentTarget.tableId).getElementsByTagName("tbody")[0],n=t.childElementCount,d=e.currentTarget.tableId.replace("table-","");let l=document.createElement("tr"),a=document.createElement("td"),c=document.createElement("input");c.type="checkbox",c.checked=!0,c.classList.add("form-control-sm"),c.id="checkbox-active-"+d+"-"+n,a.appendChild(c),l.appendChild(a);let r=document.createElement("td");r.id=d+"-key-"+n,r.contentEditable=!0,r.addEventListener("keyup",updateFullUrl),l.appendChild(r);let o=document.createElement("td");o.id=d+"-value-"+n,o.contentEditable=!0,o.addEventListener("keyup",updateFullUrl),l.appendChild(o);let u=document.createElement("td");u.id=d+"-description-"+n,u.contentEditable=!0,l.appendChild(u),t.appendChild(l)}}function updateFullUrl(){const e=document.getElementById("textbox-url"),t=document.getElementById("textbox-full-url"),n=document.getElementById("table-parameters").getElementsByTagName("tbody")[0],d=document.getElementById("protocol-switch");if(t.value=d.innerText+e.value,n.firstChild&&""!=document.getElementById("parameters-key-0").innerText){t.value+="?";for(let[e,d]of n.childNodes.entries())document.getElementById("checkbox-active-parameters-"+e).checked&&(t.value+=document.getElementById("parameters-key-"+e).innerText,""!=document.getElementById("parameters-value-"+e).innerText&&(t.value+="="+document.getElementById("parameters-value-"+e).innerText),d!=n.lastChild&&(t.value+="&"))}}async function http_request(e,t){const n=document.getElementById("textarea-response");try{const d=await fetch(e,{method:t}),l=await d.json();n.value=JSON.stringify(l,null,4),document.querySelectorAll('a[href="#response-pane"]')[0].click()}catch(e){n.value=e}}ready(add_event_listeners_to_form);